mapper.select:
SELECT * FROM club
WHERE category = #{category}
	AND wide_area = #{wideArea} 
	AND detail_area = #{detailArea} 
	AND club_name LIKE "%${keyword}%"

SELECT COUNT(*)
FROM member
WHERE club_id = #{clubId}

SELECT COUNT(*)
FROM club_like
WHERE club_id = #{clubId}

SELECT leader
FROM member
WHERE user_id = #{userId} AND club_id = #{clubId}

SELECT COUNT(*) > 0
FROM club_like
WHERE user_id = #{userId} AND club_id = #{clubId}

SELECT COUNT(*) > 0
FROM member
WHERE user_id = #{userId} AND club_id = #{clubId}
