mapper.select:
SELECT user_id, user_name, nickname, email, wide_area, detail_area
FROM user
WHERE user_id = #{userId}

SELECT COUNT(*) > 0 
FROM user 
WHERE nickname = #{nickname}

SELECT COUNT(*) > 0 
FROM user 
WHERE email = #{email}

SELECT password
FROM user 
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET password = #{encryptedNewPw}
WHERE user_id = #{userId}
